import json
import os
import uuid
from typing import Annotated
from typing import List

import numpy as np
from fastapi import APIRouter
from fastapi import Body

from harmony import convert_files_to_instruments
from harmony.matching.default_matcher import match_instruments
from harmony.schemas.requests.text import RawFile, Instrument, MatchBody, Question
from harmony.schemas.responses.text import MatchResponse

router = APIRouter(prefix="/text")

mhc_questions = []
mhc_all_metadatas = []
mhc_embeddings = np.zeros((0, 0))

try:
    data_path = os.getenv("DATA_PATH")
    with open(data_path + "/mhc_questions.json",
              "r", encoding="utf-8") as f:
        for l in f:
            mhc_question = Question.parse_raw(l)
            mhc_questions.append(mhc_question)
    with open(
            data_path + "/mhc_all_metadatas.json",
            "r", encoding="utf-8") as f:
        for l in f:
            mhc_meta = json.loads(l)
            mhc_all_metadatas.append(mhc_meta)
    with open(data_path + "/mhc_embeddings.npy",
              "rb") as f:
        mhc_embeddings = np.load(f)
except:
    print("Could not load MHC embeddings ", str(os.getcwd()))


@router.post(
    path="/parse"
)
def parse_instruments(files: Annotated[List[RawFile], Body(examples={
    "pdf": {
        "summary": "PDF document",
        "description": "A tiny PDF file in MIME+Base 64 with some plain text containing two of the GAD-7 items.",
        "value": [{
            "file_id": "d39f31718513413fbfc620c6b6135d0c",
            "file_name": "GAD-7.pdf",
            "file_type": "pdf",
            "content": "data:application/pdf;base64,"
        }
        ],
    },
    "xlsx (Openpyxl)": {
        "summary": "Excel format (Openpyxl engine)",
        "description": "A small Excel file in MIME+Base 64 format containing some items from the GAD-7.",
        "value": [{
            "file_id": "1d66bce4b80c4b0eaefe33f00cddedef",
            "file_name": "GAD-7.xlsx",
            "file_type": "xlsx",
            "content": "data:application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;base64,"
        }
        ],
    },
    "xlsx (Xlsxwriter)": {
        "summary": "Excel format (Xlsxwriter engine)",
        "description": "A small Excel file in MIME+Base 64 format containing some items from the GAD-7.",
        "value": [{
            "file_id": "1d66bce4b80c4b0eaefe33f00cddedef",
            "file_name": "GAD-7.xlsx",
            "file_type": "xlsx",
            "content": "data:application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;base64,"
        }
        ],
    },
    "txt": {
        "summary": "Plain text document",
        "description": "A small plain text file containing some items from the GAD-7.",
        "value": [{
            "file_id": "97bdeeffe67d4f5fa271724c2200f268",
            "file_name": "GAD-7.txt",
            "file_type": "txt",
            "content": "1. Feeling nervous, anxious, or on edge\tNot at all/Several days/More than half the days/Nearly every day\n2. Not being able to stop or control worrying\tNot at all/Several days/More than half the days/Nearly every day"
        }
        ],
    },
})]) -> List[Instrument]:
    """
    Parse PDFs or Excels or text files into Instruments, and identifies the language.

    If the file is binary (Excel or PDF), you must supply each file with the content in MIME format and the bytes in base64 encoding, like the example RawFile in the schema.

    If the file is plain text, supply the file content as a standard string.

    """

    for file in files:
        if file.file_id is None:
            file.file_id = uuid.uuid4().hex

    return convert_files_to_instruments(files)


@router.post(
    path="/match"
)
def match(match_body: MatchBody) -> MatchResponse:
    """
    Match instruments

    """

    # Assign any missing IDs
    for instrument in match_body.instruments:
        if instrument.file_id is None:
            instrument.file_id = uuid.uuid4().hex
        if instrument.instrument_id is None:
            instrument.instrument_id = uuid.uuid4().hex

    query = match_body.query
    if type(query) is str:
        query = query.strip()
    if query == "":
        query = None

    questions, matches, query_similarity = match_instruments(match_body.instruments, query,
                                                             mhc_questions, mhc_all_metadatas,
                                                             mhc_embeddings)

    matches_jsonifiable = matches.tolist()

    if query_similarity is not None:
        query_similarity = query_similarity.tolist()

    response = MatchResponse(questions=questions, matches=matches_jsonifiable, query_similarity=query_similarity)

    return response
